<pre class="lang:php decode:true">//查询数据

function sel_news($limit,$offset) {
 $this-&gt;db-&gt;limit($limit,$offset);
 $query = $this-&gt;db-&gt;get(&#039;news&#039;);
 $row = $query-&gt;row(&#039;navid&#039;);
 return $query-&gt;result_array();
 }

//查询数量
 function count_news() {
 $query = $this-&gt;db-&gt;get_where(&#039;news&#039;);
 return $query-&gt;num_rows();
 }</pre>
