JS代码如下：
<pre class="lang:js decode:true">&lt;script type=&quot;text/javascript&quot;&gt;
function $(id){
return document.getElementById(id);
}
window.onload=function(){
	var pan;
	var conf;
	var selectAll = $(&quot;selectAll&quot;),
	unSelect = $(&quot;unSelect&quot;),
	del = $(&quot;del&quot;),
	inputs=document.getElementsByName(&#039;range[]&#039;),
	len = inputs.length;
	selectAll.onclick=function(){
		for(var i=0; i&lt;len;i++){
			inputs[i].checked=true;
		}
	}
	unSelect.onclick=function(){
		for(var i=0; i&lt;len;i++){
			var o = inputs[i];
			o.checked?o.checked=false:o.checked=true;
		}
	}

	$(&quot;form1&quot;).onsubmit=function(){
		for(var i=0; i&lt;len;i++){
			var o = inputs[i];
			if(o.checked){
				pan=1;
				break;
			}else{
				pan=0;
			}
		}

		if(!pan){
			alert(&quot;请选择&quot;);
			return false;
		}else{
			conf=confirm(&quot;确定删除&quot;);
		}

		if(conf){
			return true;
			}else{
				return false;
				}
	}
}
&lt;/script&gt;</pre>
